import React, { Component } from 'react';
import './App.css';
import Button from './components/Button/Button.js'

let buttons = {
  multipleChoiceButton: {
    name: "multipleChoiceButton",
    text: "-4, -2, and 1",
  },
  matchmakingButton: {
    name: "matchmakingButton",
    text: "PLAY",
  },
  categoryButton: {
    name: "categoryButton",
    text: "GRE",

  }
}


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0,
      randomButton: [],
      init: true
    }
  }

  dummyFunction(e) {
    this.setState({
      counter: this.state.counter + 1
    })

    e.preventDefault()
  }

  getRandomButton() {
    let types = ["multipleChoiceButton", "matchmakingButton", "categoryButton"]
    let type = types[Math.floor(Math.random() * 3)]
    let text = buttons[type].text
    let props = {onClick: (e) => this.dummyFunction(e), text}
    return <Button 
      type={type}
      buttons={buttons}
      {...props}
    />
  }

  init() {
    if (this.state.init) {
      this.setRandomButton()
      this.setState({
        init: false
      })
    }
  }

  setRandomButton() {
    this.setState({
      randomButton: this.getRandomButton()
    })
  }


  render() {
    this.init()
    return (
      <div className="App">
        <div className="Button">
          {this.state.randomButton}
        </div>
      <div>{this.state.counter}</div>
      </div>
    );
  }
}

export default App;
